class StubVectorTileServer(
    private val tileProvider: (key: Rect<LonLat>) -> ByteArray?
) {
    private var server: EmbeddedServer<*, *>? = null

    fun start(port: Int = 0): Int {
        val server = embeddedServer(CIO, port = port) {
            install(WebSockets) {
                pingPeriod = 15.seconds
                timeout = 15.seconds
            }

            routing {
                webSocket("/tiles") {
                    for (frame in incoming) {
                        if (frame is Frame.Text) {
                            val text = frame.readText()
                            when (val request = RequestJsonParser.parse(parseJson(text))) {
                                is ConfigureConnectionRequest -> {
                                    send(Frame.Text(themeJson))
                                }
                                is GetBinaryGeometryRequest -> {
                                    //println("StubVectorTileServer: request key=${request.key}, tileData size=${tileData?.size}, zoom=${request.zoom}, bbox=${request.bbox}")
                                    val tileData = tileProvider(request.bbox)


                                    if (tileData != null) {
                                        val headerBytes = ByteArray(1 + request.key.length)
                                        headerBytes[0] = request.key.length.toByte()
                                        request.key.forEachIndexed { index, ch ->
                                            headerBytes[index + 1] = ch.code.toByte()
                                        }
                                        val rawData = tileData.drop(2)
                                        val responseData = buildByteString {
                                            append(headerBytes)
                                            append(rawData.toByteArray())
                                        }.toByteArray()

                                        send(Frame.Binary(true, responseData))
                                    }
                                }
                                is Request.CancelBinaryTileRequest -> {
                                    // Handle cancellation if needed
                                }
                            }
                        }
                    }
                }
            }
        }.start(wait = false)

        val port = runBlocking { server.engine.resolvedConnectors().first().port }
        this.server = server
        return port
    }

    fun stop() {
        server?.stop(0, 0)
    }
}

