val defaultTiles = mapOf(
    Rect.XYWH<LonLat>(-180, -90, 360, 180) to Base64.decode(""""""),
    Rect.XYWH<LonLat>(0, -90, 180, 180) to Base64.decode(""""""),
    Rect.XYWH<LonLat>(-180, -90, 360, 90) to Base64.decode(""""""),
    Rect.XYWH<LonLat>(0, -90, 180, 90) to Base64.decode(""""""),
)
/**
 * @param tiles A map where key is the tile identifier (e.g. "0") and value is the binary vector data.
 */
fun runStubVectorTileServer(
    tiles: Map<Rect<LonLat>, ByteArray> = defaultTiles,
    block: (url: String) -> Unit
) {
    val server = StubVectorTileServer(
        tileProvider = { key -> tiles[key] }
    )

    // Using 0 to auto-find a port (assuming your start() logic handles this)
    // For simplicity here, we'll assume a start(port) or your robust start()
    val port = server.start()

    val url = "ws://127.0.0.1:$port/tiles"

    try {
        runBlocking {
            block(url)
        }
    } finally {
        server.stop()
    }
}
